import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({contacts,title,setTitle,contact,setContact,date,setDate,time,setTime,handleSubmit}) => {

// AppointmentForm Requirements:

// Render a form with:
// 3 controlled input components, to be used for the name, date and time appointment data
// A ContactPicker component with the contacts list passed in via props
// A submit button
// Use getTodayString() to set the min attribute of the date input

  return (
    <>
    <form onSubmit={handleSubmit}>
      <input value={contact} placeholder="Contact" type="text"/>
      <input value={date} placeholder="Date" />
      <input value={time} placeholder="Time" />

    </form>
    </>
  );
};
