import React, { useState } from "react";

import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({appointments,contacts, newAppointment }) => {
  /*
  Define state variables for 
  appointment info
  */
  const [name, setName] = useState('');
  const [contact, setContact] = useState('');
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  

  // Receive three props:
  // V The current list of appointments 
  // The current list of contacts
  // A callback function for adding a new appointment
  // Keep track of four local state variables, the current name, contact, date, and time entered into the form
  // Add a new appointment on form submission
  // Clear the form on submission
  // In the Add Appointment section, render an AppointmentForm with the following passed via props:
  // local state variables
  // local state variable setter functions
  // handleSubmit callback function
  // In the Appointments section, render a TileList with the appointment array passed via props


  const handleSubmit = (e) => {
    e.preventDefault();
    /*
    Add contact info and clear data  
    */
    newAppointment(name, contact, date, time);
    
   setName('');
   setContact('');
   setDate('');
   setTime('');
   //should call the callback function for adding a new appointment with the data from the form.
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm {...{name, contact, date, time, setContact,setDate,setTime,setName, handleSubmit}} />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
        {/* In the Appointments section, render a TileList with the appointment array passed via props */}
      </section>
    </div>
  );
};
