import React from "react";

const getTodayString = () => {
  const [month, day, year] = new Date()
    .toLocaleDateString("en-US")
    .split("/");
  return `${year}-${month.padStart(2, "0")}-${day.padStart(2, "0")}`;
};

export const AppointmentForm = ({name,contacts,title,setTitle,contact,setContact,date,setDate,time,setTime,handleSubmit}) => {


  const handleName = (e) => {
    setName(e.target.value);
  };

  
// AppointmentForm Requirements:

// Render a form with:
// A ContactPicker component with the contacts list passed in via props
// A submit button
// Use getTodayString() to set the min attribute of the date input


  return (
    <>
    <form onSubmit={handleSubmit}>
      <input value={name} placeholder="Name" type="text" onChange={handleName}/>
      <input value={date} placeholder="Date" type="date" />
      <input value={time} placeholder="Time" type="time" />
    </form>
    </>
  );
};
